import "./nav.css";

const Nav = () => {
  return (
    <header>
      <div class="logo">
        <a href="#">My Portfolio</a>
      </div>
      <nav>
        <ul>
          <li>
            <a href="#">Home</a>
          </li>
          <li>
            <a href="#">About</a>
          </li>
          <li>
            <a href="#">Portfolio</a>
          </li>
          <li>
            <a href="#">Contact</a>
          </li>
        </ul>
      </nav>
    </header>
  );
  // Get the header element
  const header = document.querySelector("header");

  // Create a new div to hold the navigation links
  const navLinks = document.createElement("div");
  navLinks.classList.add("nav-links");

  // Get the navigation links
  const nav = document.querySelector("nav");
  const navItems = nav.querySelectorAll("li");

  // Append the navigation links to the new div
  navItems.forEach((item) => {
    navLinks.appendChild(item);
  });

  // Append the new div to the header
  header.appendChild(navLinks);

  // Create a new button element for the mobile menu toggle
  const mobileMenuToggle = document.createElement("button");
  mobileMenuToggle.classList.add("mobile-menu-toggle");
  mobileMenuToggle.innerHTML = "Menu";
  header.appendChild(mobileMenuToggle);

  // Toggle the class "active" on the mobile menu toggle and the navigation links when the button is clicked
  mobileMenuToggle.addEventListener("click", () => {
    mobileMenuToggle.classList.toggle("active");
    navLinks.classList.toggle("active");
  });
};

export default Nav;
